import React, { Component } from "react";
import { StyleSheet, View, Modal, Image, Clipboard, ToastAndroid, AlertIOS, Platform } from "react-native";
import { Container, Text, Icon } from "native-base";
import Share, {ShareSheet, Button} from 'react-native-share';
import { translate } from 'react-i18next';
import i18n from 'i18next';
import DienBienAPI from '../api/index';

//  facebook icon
const FACEBOOK_ICON = "data:image/png;base64,";
//  gplus icon
const GOOGLE_PLUS_ICON = "data:image/png;base64,";
//  clipboard icon
const CLIPBOARD_ICON = "data:image/png;base64,";
//  more icon
const MORE_ICON = "data:image/png;base64,";

class ModalShare extends Component {
  constructor(props) {
    super(props);
  }
    onCancel() {
      this.props.onCancel();
    }
    render() {
    const { t, i18n } = this.props;
    let currentYear = (new Date()).getFullYear();
    // console.warn(this.props.title);
    let shareOptions = {
      title: this.props.title ? this.props.title : t('home:address') + currentYear,
      message: t('home:address'),
      url: this.props.link ? this.props.link : DienBienAPI.getServerURL() + "/",
      subject: "Share Link" //  for email
    };
    return (
        <ShareSheet visible={this.props.visibleShare} onCancel={this.onCancel.bind(this)}>
          <Button iconSrc={{ uri: FACEBOOK_ICON }}
                  onPress={()=>{
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  "social": "facebook"
                }));
              },300);
            }}>Facebook</Button>
          {/* <Button iconSrc={{ uri: GOOGLE_PLUS_ICON }}
                  onPress={()=>{
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  "social": "googleplus"
                }));
              },300);
            }}>Google +</Button> */}
          <Button
            iconSrc={{ uri: CLIPBOARD_ICON }}
            onPress={()=>{
              this.onCancel();
              setTimeout(() => {
                if(typeof shareOptions["url"] !== undefined) {
                  Clipboard.setString(shareOptions["url"]);
                  if (Platform.OS === "android") {
                    ToastAndroid.show('Link copiado al portapapeles', ToastAndroid.SHORT);
                  } else if (Platform.OS === "ios") {
                    AlertIOS.alert('Link copiado al portapapeles');
                  }
                }
              },300);
            }}>Copy Link</Button>
          <Button iconSrc={{ uri: MORE_ICON }}
            onPress={()=>{
              this.onCancel();
              setTimeout(() => {
                Share.open(shareOptions)
              },300);
            }}>More</Button>
        </ShareSheet>
    );
  }
}
export default translate(['ModalShare'], { wait: true })(ModalShare);
const styles = StyleSheet.create({
});
